// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;


interface IWishlist {
    event WishAdded(address indexed user, string indexed wish);

    function baseNode() external view returns (bytes32);

    function wishCounts(bytes32 namehash) external view returns (uint256);

    function setWishes(string[] memory name) external;

    function needAuction(string memory name) external view returns (bool);

    function userWishes(address user) external view returns (string[] memory);

    function userHasWish(address user, string memory name) external view returns (bool);

    // Dev side will add some globally reserved domains to let all users be able to join the 
    // auction for the added domains
    function addReservedNames(string[] memory names) external;
}

contracts/business/MIDRegistrarMigration.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.4;

import "hardhat/console.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/structs/BitMaps.sol";
import "../oracles/PriceOracle.sol";
import "../midregistrar/BaseRegistrarImplementation.sol";
import "../midregistrar/StringUtils.sol";
import "../resolvers/Resolver.sol";

/**
 * This contract supports users to migrate their domains from other domain services
 * into our service with a premium in price
 */
interface SourceRegistry {
    function owner(bytes32 node) external view returns (address);
}

interface SourceBaseRegistrar {
    function nameExpires(uint256 tokenId) external view returns (uint256);
}

contract MIDRegistrarMigrationController is Ownable {
    using BitMaps for BitMaps.BitMap;
    using StringUtils for *;

    SourceRegistry public sourceRegistry;
    SourceBaseRegistrar public sourceBaseRegistrar;

    address public treasury;

    PriceOracle public priceOracle;

    uint256 public constant MIN_NAME_LENGTH = 2;

    uint256 public durationRoundUpUnit = 86400 * 30 * 3; // 3 months

    /**
     * @dev discount percentage
     *  e.g [50, 60, 70, 80, 90] => For names in length 1->5, the discounts: 50%, 60%, 70%, 80%, 90%
     */
    uint256[] public discountPercentages;

    /**
     * @dev key: token id
     */
    BitMaps.BitMap migrationRecords;

    /**
     * @dev only in the period of [start, end], the discount is enabled
     */
    uint256 public start;
    uint256 public end;

    BaseRegistrarImplementation public base;

    event Migrated(
        string labelname,
        address indexed account,
        uint256 indexed cost,
        uint256 expiry
    );

    constructor(
        BaseRegistrarImplementation base_,
        PriceOracle priceOracle_, 
        SourceRegistry sourceRegistry_,
        SourceBaseRegistrar sourceBaseRegistrar_,
        address treasury_,
        uint256 start_,
        uint256 end_,
        uint256[] memory discountPercentages_
    ) {
        base = base_;
        start = start_;
        end = end_;
        sourceRegistry = sourceRegistry_;
        sourceBaseRegistrar = sourceBaseRegistrar_;
        treasury = treasury_;
        priceOracle = priceOracle_;
        setDiscountPercentages(discountPercentages_);
    }

    function valid(string memory labelname) public pure returns(bool) {
        return labelname.strlen() >= MIN_NAME_LENGTH;
    }

    function available(string memory labelname) public view returns(bool) {
        bytes32 label = keccak256(bytes(labelname));
        return valid(labelname) && base.available(uint256(label));
    }

    function setDiscountPercentages(uint256[] memory discountPercentages_) public onlyOwner {
        require(discountPercentages_.length > 0, "empty discount percentages");
        discountPercentages = discountPercentages_;
    }

    function setDurationRoundUpUnit(uint256 durationRoundUpUnit_) external onlyOwner {
        require(durationRoundUpUnit_ > 0, "invalid roundup unit");
        durationRoundUpUnit = durationRoundUpUnit_;
    }

    function setPriceOracle(PriceOracle _prices) external onlyOwner {
        require(address(_prices) != address(0), "invalid address");
        priceOracle = _prices;
    }

    function setActivePeriod(uint256 start_, uint256 end_) external onlyOwner {
        require(start_ < end_ && start_ > 0, "invalid period");
        start = start_;
        end = end_;
    }

    function setTreasury(address treasury_) external onlyOwner {
        treasury = treasury_;
    }

    /**
     * @dev rent price with discount
     */
    function rentPriceWithDuration(string memory labelname, uint256 duration)
        public
        view
        returns (uint256)
    {
        uint256 len = labelname.strlen();
        bytes32 tokenId = keccak256(bytes(labelname));
        uint256 price = priceOracle.price(labelname, base.nameExpires(uint256(tokenId)), duration);
        if(len > discountPercentages.length) {
            len = discountPercentages.length;
        }
        return price * discountPercentages[len - 1] / 100;
    }

    function rentPrice(string memory labelname) public view returns (uint256) {
        bytes32 labelHash = keccak256(bytes(labelname));
        uint256 tokenId = uint256(labelHash);
        uint256 duration = migratedDuration(tokenId);
        return rentPriceWithDuration(labelname, duration);
    }


    /**
     * @dev From Openzepplin, returns the ceiling of the division of two numbers.
     *
     * This differs from standard division with `/` in that it rounds up instead
     * of rounding down.
     */
    function ceilDiv(uint256 a, uint256 b) internal pure returns (uint256) {
        // (a + b - 1) / b can overflow on addition, so we distribute.
        return a == 0 ? 0 : (a - 1) / b + 1;
    }

    /**
     * @dev returns whether the label name is migrated or not, the input is label name without .bnb suffix
     */
    function isNameOwner(address owner, bytes32 nodehash)
        public
        view
        returns (bool)
    {
        return sourceRegistry.owner(nodehash) == owner;
    }

    /**
     * @dev returns whether the label name is migrated or not, the input is label name without .bnb suffix
     */
    function isNameMigrated(bytes32 nodehash)
        public
        view
        returns (bool)
    {
        return migrationRecords.get(uint256(nodehash));
    }

    /**
     * @dev returns the full name hash, if the labelname.bnb, the input is label name without .bnb suffix
     */
    function nodeHashByLabel(string memory labelname)
        public
        view
        returns (bytes32)
    {
        return keccak256(abi.encodePacked(base.baseNode(), keccak256(bytes(labelname))));
    }

    /**
     * @dev calculate the duration of migrated domains, domains in grace period
     *  cannot be migrated
     */
    function migratedDuration(uint256 id) public view returns (uint256) {
        uint256 sourceExpiry = sourceBaseRegistrar.nameExpires(id);
        if (sourceExpiry < block.timestamp) {
            return 0;
        }
        // round up
        uint256 remaining = sourceExpiry - block.timestamp;
        uint256 patchedRemaining = ceilDiv(remaining, durationRoundUpUnit) * durationRoundUpUnit;
        return patchedRemaining;
    }

    /**
     * @dev migrate .bnb name to our service,
     */
    function migrateWithResolver(string memory labelname, address resolver) external payable {
        require(
            block.timestamp > start && block.timestamp < end,
            "migration not available"
        );

        bytes32 labelHash = keccak256(bytes(labelname));
        uint256 tokenId = uint256(labelHash);
        bytes32 nodehash = nodeHashByLabel(labelname);

        require(isNameOwner(msg.sender, nodehash), "not the source name owner");
        // the following checking might be duplicated, but it's ok
        require(!isNameMigrated(nodehash), "name already migrated");
        require(available(labelname), "not available");

        uint duration = migratedDuration(tokenId);
        require(duration > 0, "source token might be expired"); // expired

        // the cost with discount
        uint cost = rentPriceWithDuration(labelname, duration);

        uint256 expiry;
        require(resolver != address(0), "empty resolver");

        // Set this contract as the (temporary) owner, giving it
        // permission to set up the resolver.
        expiry = base.register(tokenId, address(this), duration);

        // Set the resolver
        base.mid().setResolver(nodehash, resolver);

        // Configure the resolver
        Resolver(resolver).setAddr(nodehash, msg.sender);

        // Now transfer full ownership to the expeceted owner
        base.reclaim(tokenId, msg.sender);
        base.transferFrom(address(this), msg.sender, tokenId);

        emit Migrated(labelname, msg.sender, cost, expiry);

        // transfer the revenue to treasury
        require(msg.value >= cost, "insufficient payment");
        if (treasury != address(0)) {
            payable(treasury).transfer(cost);
        }

        // Refund any extra payment
        if (msg.value > cost) {
            payable(msg.sender).transfer(msg.value - cost);
        }

        migrationRecords.set(uint256(nodehash));
    }

    function withdraw() public onlyOwner {
        payable(msg.sender).transfer(address(this).balance);
    }

}

contracts/business/Wishlist.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "@openzeppelin/contracts/access/Ownable.sol";
import "../midregistrar/StringUtils.sol";
import "./IWishlist.sol";

contract Wishlist is Ownable, IWishlist {
    using StringUtils for *;

    mapping (bytes32 => uint256) override public wishCounts;

    mapping (address => string[]) public wishes;

    bytes32 override public baseNode;

    // wish list limit per user
    uint256 public wishCap;

    // wishlist phrase period
    uint256 public wishPhraseStart;
    uint256 public wishPhraseEnd;

    // reserved names for global auction, token id => availability
    mapping (bytes32 => bool) public reservedNameMap;
    string[] public reservedNames;

    constructor(uint256 wishCap_, uint256 wishPhraseStart_, uint256 wishPhraseEnd_, bytes32 baseNode_) {
        setWishCap(wishCap_);
        setWishPhraseTime(wishPhraseStart_, wishPhraseEnd_);
        setBaseNode(baseNode_);
    }

    function setWishCap(uint256 wishCap_) public onlyOwner {
        require(wishCap_ > 0, "invalid parameters");
        wishCap = wishCap_;
    }

    function setWishPhraseTime(uint256 wishPhraseStart_, uint256 wishPhraseEnd_) public onlyOwner {
        require(wishPhraseStart_ > 0 && wishPhraseStart_ < wishPhraseEnd_, "invalid parameters");
        wishPhraseStart = wishPhraseStart_;
        wishPhraseEnd = wishPhraseEnd_;
    }

    function setBaseNode(bytes32 baseNode_) public onlyOwner {
        require(baseNode_ != bytes32(0), "invalid parameters");
        baseNode = baseNode_;
    }

    function blocktime() public view returns (uint) {
        return block.timestamp;
    } 

    function setWishes(string[] memory names) override external {
        require(block.timestamp > wishPhraseStart && block.timestamp < wishPhraseEnd, "not wishlist phrase");
        require(names.length == wishCap, "wrong wish number");

        string[] storage currentNames = wishes[msg.sender];
        for (uint256 i = 0; i < names.length; ++i) {
            string memory name = names[i];
            bytes32 namehash = keccak256(bytes(name));
            require(names[i].strlen() > 0, "empty name");

            // check duplicates
            for (uint256 j = 0; j < names.length; ++j) {
                if (i != j) {
                    require(keccak256(bytes(names[j])) != namehash, "duplicated wish");
                }
            }

            // if this user never wished this name, increase count
            bool hasWished;
            for (uint256 j = 0; j < currentNames.length; ++j) {
                hasWished = keccak256(bytes(currentNames[j])) == namehash;
                if (hasWished) {
                    break;
                }
            }
            if (!hasWished) {
                wishCounts[namehash]++;
            }

            emit WishAdded(msg.sender, name);
        }

        wishes[msg.sender] = names;
    }

    // if more than 1 user wished this name, this name need auction
    function needAuction(string memory name) override external view returns (bool) {
        return wishCounts[keccak256(bytes(name))] > 1;
    }

    function userWishes(address user) override external view returns (string[] memory) {
        return wishes[user];
    }

    function userHasWish(address user, string memory name) override public view returns (bool) {
        bytes32 namehash = keccak256(bytes(name));
        string[] storage names = wishes[user];
        for (uint256 i = 0; i < names.length; i++) {
            if(keccak256(bytes(names[i])) == namehash) {
                return true;
            }
        }
        return false;
    }

    function addReservedNames(string[] memory names) override external onlyOwner {
        for (uint256 i = 0; i < names.length; ++i) {
            bytes32 namehash = keccak256(bytes(names[i]));
            require(!reservedNameMap[namehash], "duplicated name");
            reservedNameMap[namehash] = true;
            reservedNames.push(names[i]);
        }
    }

}

contracts/midregistrar/BaseRegistrar.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "../registry/MID.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

abstract contract BaseRegistrar is Ownable, IERC721 {
    uint256 public gracePeriod = 30 days;

    event ControllerAdded(address indexed controller);
    event ControllerRemoved(address indexed controller);
    event NameMigrated(uint256 indexed id, address indexed owner, uint expires);
    event NameRegistered(uint256 indexed id, address indexed owner, uint expires);
    event NameRenewed(uint256 indexed id, uint expires);

    // The MID registry
    MID public mid;

    // The namehash of the TLD this registrar owns (eg, .eth)
    bytes32 public baseNode;

    // A map of addresses that are authorised to register and renew names.
    mapping(address=>bool) public controllers;

    // Reset the grace period
    function setGracePeriod(uint256 gracePeriod_) virtual external;

    // Authorises a controller, who can register and renew domains.
    function addController(address controller) virtual external;

    // Revoke controller permission for an address.
    function removeController(address controller) virtual external;

    // Set the resolver for the TLD this registrar manages.
    function setResolver(address resolver) virtual external;

    // Returns the expiration timestamp of the specified label hash.
    function nameExpires(uint256 id) virtual external view returns(uint);

    // Returns true iff the specified name is available for registration.
    function available(uint256 id) virtual public view returns(bool);

    /**
     * @dev Register a name.
     */
    function register(uint256 id, address owner, uint duration) virtual external returns(uint);

    function renew(uint256 id, uint duration) virtual external returns(uint);

    /**
     * @dev Reclaim ownership of a name in MID, if you own it in the registrar.
     */
    function reclaim(uint256 id, address owner) virtual external;
}

contracts/midregistrar/BaseRegistrarImpl.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.4;

import "hardhat/console.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/IERC721Enumerable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "../registry/MID.sol";
import "./BaseRegistrar.sol";

contract BaseRegistrarImplementation is
    Pausable,
    ERC721,
    IERC721Enumerable,
    BaseRegistrar
{
    /**
     * @dev A map of expiry times
     */
    mapping(uint256 => uint256) expiries;

    /**
     * @dev Base URI of NFT metadata
     */
    string public baseURI;

    /**
     * @dev All minted token ids
     */
    uint256[] internal tokens;

    /**
     * @dev The max minting count per account
     */
    uint256 public maxMintPerUser = 10e18;

    /**
     * @dev mapping from token id to index in tokens array
     */
    mapping(uint256 => uint256) internal idToIndexes;

    /**
     * @dev mapping from owner address to token ids
     */
    mapping(address => uint256[]) internal ownerToIds;

    bytes4 private constant INTERFACE_META_ID =
        bytes4(keccak256("supportsInterface(bytes4)"));
    bytes4 private constant ERC721_ID =
        bytes4(
            keccak256("balanceOf(address)") ^
                keccak256("ownerOf(uint256)") ^
                keccak256("approve(address,uint256)") ^
                keccak256("getApproved(uint256)") ^
                keccak256("setApprovalForAll(address,bool)") ^
                keccak256("isApprovedForAll(address,address)") ^
                keccak256("transferFrom(address,address,uint256)") ^
                keccak256("safeTransferFrom(address,address,uint256)") ^
                keccak256("safeTransferFrom(address,address,uint256,bytes)")
        );
    bytes4 private constant RECLAIM_ID =
        bytes4(keccak256("reclaim(uint256,address)"));

    event BaseURIChanged(string indexed URI);
    event MaxMintCapChanged(uint256 indexed cap);
    event GracePeriodChanged(uint256 indexed period);

    /**
     * v2.1.3 version of _isApprovedOrOwner which calls ownerOf(tokenId) and takes grace period into consideration instead of ERC721.ownerOf(tokenId);
     * https://github.com/OpenZeppelin/openzeppelin-contracts/blob/v2.1.3/contracts/token/ERC721/ERC721.sol#L187
     * @dev Returns whether the given spender can transfer a given token ID
     * @param spender address of the spender to query
     * @param tokenId uint256 ID of the token to be transferred
     * @return bool whether the msg.sender is approved for the given token ID,
     *    is an operator of the owner, or is the owner of the token
     */
    function _isApprovedOrOwner(address spender, uint256 tokenId)
        internal
        view
        override
        returns (bool)
    {
        address owner = ownerOf(tokenId);
        return (spender == owner ||
            getApproved(tokenId) == spender ||
            isApprovedForAll(owner, spender));
    }

    constructor(MID _mid, bytes32 _baseNode) ERC721("Monster Domain", "MID") {
        mid = _mid;
        baseNode = _baseNode;
    }

    modifier live() {
        require(mid.owner(baseNode) == address(this), "not live");
        _;
    }

    modifier onlyController() {
        require(controllers[msg.sender], "only ctrl");
        _;
    }

    /**
     * @dev Pause the register & renew globally
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause the register & renew globally
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Gets the owner of the specified token ID. Names become unowned
     *      when their registration expires.
     * @param tokenId uint256 ID of the token to query the owner of
     * @return address currently marked as the owner of the given token ID
     */
    function ownerOf(uint256 tokenId)
        public
        view
        override(IERC721, ERC721)
        returns (address)
    {
        require(expiries[tokenId] > block.timestamp, "expired");
        return super.ownerOf(tokenId);
    }

    /**
     * @dev Reset the grace period
     */
    function setGracePeriod(uint256 gracePeriod_) external override onlyOwner {
        gracePeriod = gracePeriod_;
        emit GracePeriodChanged(gracePeriod);
    }

    /**
     * @dev Authorises a controller, who can register and renew domains.
     */
    function addController(address controller) external override onlyOwner {
        controllers[controller] = true;
        emit ControllerAdded(controller);
    }

    /**
     * @dev Revoke controller permission for an address.
     */
    function removeController(address controller) external override onlyOwner {
        controllers[controller] = false;
        emit ControllerRemoved(controller);
    }

    /**
     * @dev Set the resolver for the TLD this registrar manages.
     */
    function setResolver(address resolver) external override onlyOwner {
        mid.setResolver(baseNode, resolver);
    }

    /**
     * @dev Returns the expiration timestamp of the specified id.
     */
    function nameExpires(uint256 id) external view override returns (uint256) {
        return expiries[id];
    }

    /**
     * @dev Returns true iff the specified name is available for registration.
     */
    function available(uint256 id) public view override returns (bool) {
        // Not available if it's registered here or in its grace period.
        return expiries[id] + gracePeriod < block.timestamp;
    }

    /**
     * @dev Register a name.
     * @param id The token ID (keccak256 of the label).
     * @param owner The address that should own the registration.
     * @param duration Duration in seconds for the registration.
     */
    function register(
        uint256 id,
        address owner,
        uint256 duration
    ) external override returns (uint256) {
        return _register(id, owner, duration, true);
    }

    /**
     * @dev Register a name, without modifying the registry.
     * @param id The token ID (keccak256 of the label).
     * @param owner The address that should own the registration.
     * @param duration Duration in seconds for the registration.
     */
    function registerOnly(
        uint256 id,
        address owner,
        uint256 duration
    ) external returns (uint256) {
        return _register(id, owner, duration, false);
    }

    function _register(
        uint256 id,
        address owner,
        uint256 duration,
        bool updateRegistry
    ) internal live onlyController whenNotPaused returns (uint256) {
        require(available(id), "not available");
        require(
            block.timestamp + duration + gracePeriod >
                block.timestamp + gracePeriod,
            "timestamp overflow"
        ); // Prevent future overflow

        // balance check
        require(balanceOf(owner) < maxMintPerUser, "balance exceeds cap");

        expiries[id] = block.timestamp + duration;
        if (_exists(id)) {
            // Name was previously owned, and expired
            _burn(id);
        }
        _mint(owner, id);
        if (updateRegistry) {
            mid.setSubnodeOwner(baseNode, bytes32(id), owner);
        }

        emit NameRegistered(id, owner, block.timestamp + duration);

        return block.timestamp + duration;
    }

    /**
     * @dev Renew the name with input duration
     */
    function renew(uint256 id, uint256 duration)
        external
        override
        live
        onlyController
        whenNotPaused
        returns (uint256)
    {
        require(
            expiries[id] + gracePeriod >= block.timestamp,
            "still in grace period"
        ); // Name must be registered here or in grace period
        require(
            expiries[id] + duration + gracePeriod > duration + gracePeriod,
            "timestamp overflowed"
        ); // Prevent future overflow

        expiries[id] += duration;
        emit NameRenewed(id, expiries[id]);
        return expiries[id];
    }

    /**
     * @dev Reclaim ownership of a name in MID, if you own it in the registrar.
     */
    function reclaim(uint256 id, address owner) external override live whenNotPaused {
        require(_isApprovedOrOwner(msg.sender, id), "not approved or owner");
        mid.setSubnodeOwner(baseNode, bytes32(id), owner);
    }

    /**
     * @dev Support directly set the baseURI.
     * @param baseURI_ the new base URI
     */
    function setBaseURI(string memory baseURI_) external onlyOwner {
        baseURI = baseURI_;
        emit BaseURIChanged(baseURI);
    }

    /**
     * @dev Return the metadata URI by token ID
     */
    function tokenURI(uint256 tokenId)
        public
        view
        override
        returns (string memory)
    {
        require(_exists(tokenId), "tokenId does not exist");
        return
            bytes(baseURI).length > 0
                ? string(abi.encodePacked(baseURI, Strings.toString(tokenId)))
                : "";
    }

    /**
     * @dev Support token metadata
     */
    function totalSupply() external view override returns (uint256) {
        return tokens.length;
    }

    /**
     * @dev Get token ID by stored order
     */
    function tokenByIndex(uint256 _index)
        external
        view
        override
        returns (uint256)
    {
        require(_index < tokens.length, "invalid index");
        return tokens[_index];
    }

    function tokenOfOwnerByIndex(address _owner, uint256 _index)
        external
        view
        override
        returns (uint256)
    {
        require(_index < ownerToIds[_owner].length, "invalid index");
        return ownerToIds[_owner][_index];
    }

    function tokensOfOwner(address _owner) external view returns (uint256[] memory) {
        return ownerToIds[_owner];
    }

    function _mint(address _to, uint256 _tokenId) internal virtual override {
        super._mint(_to, _tokenId);
        tokens.push(_tokenId);
        idToIndexes[_tokenId] = tokens.length - 1;
    }

    function _burn(uint256 _tokenId) internal virtual override {
        super._burn(_tokenId);
        uint256 tokenIndex = idToIndexes[_tokenId];
        uint256 lastTokenIndex = tokens.length - 1;
        uint256 lastToken = tokens[lastTokenIndex];
        tokens[tokenIndex] = lastToken;
        tokens.pop();
        // This wastes gas if you are burning the last token but saves a little gas if you are not.
        idToIndexes[lastToken] = tokenIndex;
        idToIndexes[_tokenId] = 0;
    }

    /**
     * @dev modify `transferFrom` and `safeTransferFrom` to make the domain automatically
     *  transfer to target owner as well
     */
    function transferFrom(
        address from,
        address to,
        uint256 tokenId
    ) public override(ERC721, IERC721) {
        require(
            _isApprovedOrOwner(_msgSender(), tokenId),
            "ERC721: caller is not approved or owner"
        );
        _transfer(from, to, tokenId);
        mid.setSubnodeOwner(baseNode, bytes32(tokenId), to);
    }

    /**
     * @dev modify `transferFrom` and `safeTransferFrom` to make the domain automatically
     *  transfer to target owner as well
     */
    function safeTransferFrom(
        address from,
        address to,
        uint256 tokenId,
        bytes memory _data
    ) public override(ERC721, IERC721) {
        require(
            _isApprovedOrOwner(_msgSender(), tokenId),
            "ERC721: caller is not approved or owner"
        );
        _safeTransfer(from, to, tokenId, _data);
        mid.setSubnodeOwner(baseNode, bytes32(tokenId), to);
    }

    /**
     * @dev max mint per user setter
     */
    function setMaxMintPerUser(uint256 cap) onlyOwner external {
        require(cap > 0, "invalid mint cap");
        maxMintPerUser = cap;
        emit MaxMintCapChanged(cap);
    }

    function supportsInterface(bytes4 interfaceID)
        public
        pure
        override(ERC721, IERC165)
        returns (bool)
    {
        return
            interfaceID == INTERFACE_META_ID ||
            interfaceID == ERC721_ID ||
            interfaceID == RECLAIM_ID;
    }
}

